import React from "react";
import { IonPage, IonHeader, IonToolbar, IonTitle, IonContent, IonCard, IonCardHeader, IonCardTitle,  IonGrid, IonRow, IonCol, IonList, IonItem, IonSelect, IonSelectOption } from "@ionic/react";
import{Line, Pie} from "react-chartjs-2";

import "../css/Evolucao.css";

//----------------------------------------------------------------------
//Graph settings

var generalGraphSettings = { 
    labels: [],
    datasets: [{ 
        fill: false, lineTension: 0.1,
        backgroundColor: 'rgba(75,192,192,0.4)', borderColor: 'rgba(75,192,192,1)',
        borderCapStyle: 'butt', borderDash: [], borderDashOffset: 0.0, borderJoinStyle: 'miter',
        pointBorderColor: 'rgba(75,192,192,1)', pointBackgroundColor: '#fff',
        pointBorderWidth: 5, pointHoverRadius: 5,
        pointHoverBackgroundColor: 'rgba(75,192,192,1)', pointHoverBorderColor: 'rgba(220,220,220,1)',
        pointHoverBorderWidth: 2, pointRadius: 3, pointHitRadius: 10, 
        data: []
    }]
};

var pieGraphSettings = {
	labels: [ 'Massa Gorda', 'Massa Magra'],
	datasets: [{ data: [300, 50], backgroundColor: [ '#FF6384', '#36A2EB'],
		hoverBackgroundColor: [ '#FF6384', '#36A2EB' ] }]
};

//----------------------------------------------------------------------

interface registoAvaliacao {
    data: Date,
    y: number
}

//----------------------------------------------------------------------

class Evolucao extends React.Component {

    state: {

        peso: Array<registoAvaliacao>
        massaGorda: Array<registoAvaliacao>
        massaMagra: Array<registoAvaliacao>

        cintura: Array<registoAvaliacao>

        menuOpt: String
    }

    constructor(props: any) {

        super(props);   

        this.state = ({

            peso: new Array<registoAvaliacao>(),
            massaGorda: new Array<registoAvaliacao>(),
            massaMagra: new Array<registoAvaliacao>(),

            cintura: new Array<registoAvaliacao>(),

            //default option
            menuOpt: "compoCorporalOption"
        });
    }

    componentDidMount() {

        this.setState({
            peso: [
                {data: new Date(2020, 1, 3), y: 98},
                {data: new Date(2020, 3, 3), y: 95},
                {data: new Date(2020, 6, 4), y: 93},
                {data: new Date(2020, 8, 3), y: 88},
                {data: new Date(2020, 10, 10), y: 82}
            ],
            massaGorda: [
                {data: new Date(2020, 1, 3), y: 24},
                {data: new Date(2020, 3, 3), y: 23},
                {data: new Date(2020, 6, 4), y: 18},
                {data: new Date(2020, 8, 3), y: 15},
                {data: new Date(2020, 10, 10), y: 18}
            ],
            massaMagra: [
                {data: new Date(2020, 1, 3), y: 70},
                {data: new Date(2020, 3, 3), y: 71},
                {data: new Date(2020, 6, 4), y: 74},
                {data: new Date(2020, 8, 3), y: 73.5},
                {data: new Date(2020, 10, 10), y: 73}
            ],
            cintura: [
                {data: new Date(2020, 1, 3), y: 108},
                {data: new Date(2020, 3, 3), y: 95},
                {data: new Date(2020, 6, 4), y: 83},
                {data: new Date(2020, 8, 3), y: 83},
                {data: new Date(2020, 10, 10), y: 84}
            ],
        });
    }

    render() {

        //----------------------------------------------------------------------

        const noLegend={ legend: { display: false} }; 

        //----------------------------------------------------------------------

        var xPeso: any[] = [], yPeso: number[] = [];
        this.state.peso.forEach((ref)=>{
            xPeso.push(""+(ref.data.getMonth())+"/"+ref.data.getDay());
            yPeso.push(ref.y);
        });
        var dataPeso: any = JSON.parse(JSON.stringify(generalGraphSettings));
        dataPeso.labels = xPeso;
        dataPeso.datasets[0].data = yPeso;

        //----------------------------------------------------------------------

        var dataMG_MM_PIE: any = JSON.parse(JSON.stringify(pieGraphSettings));
        dataPeso.datasets[0].data = [this.state.massaGorda.pop(), 
                                     this.state.massaMagra.pop()];

        //----------------------------------------------------------------------

        var xCintura : any[] = [], yCintura: number[] = [];
        this.state.cintura.forEach((ref) => {
            xCintura.push(""+(ref.data.getMonth())+"/"+ref.data.getDay());
            yCintura.push(ref.y);
        });
        var dataCintura: any = JSON.parse(JSON.stringify(generalGraphSettings));
        dataCintura.labels = xCintura;
        dataCintura.datasets[0].data = yCintura;

        //----------------------------------------------------------------------

        var menuOp = this.state.menuOpt;

        if(this.state.menuOpt !== menuOp) {

            this.setState( { menuOpt: menuOp } )
        } 

        //----------------------------------------------------------------------

        return(

            <IonPage>

                {/* Page title */}

                <IonHeader>
                    <IonToolbar color="primary">
                        <IonTitle id="page-title">Evolução</IonTitle>
                    </IonToolbar>
                </IonHeader>

                {/* Menu superior para escolha de grupo de gráficos */}

                <IonList>

                    {/*
                    <IonListHeader>
                        <IonLabel></IonLabel>
                    </IonListHeader>
                    */}

                    <IonItem className="SelectionMenuItem">

                            <IonSelect value={this.state.menuOpt} placeholder="Select One" onIonChange={
                                e =>this.setState( {menuOpt:e.detail.value})
                            }>

                                <IonSelectOption value="compoCorporalOption"><div className="SMlabel">Composição corporal</div></IonSelectOption>
                                <IonSelectOption value="perimCorporalOption"><div className="SMlabel">Perímetros corporais</div></IonSelectOption>

                            </IonSelect>
                    </IonItem>

                </IonList>

                {/* Gráficos disponiveis */}

                <IonContent className="ionContent">

                <IonGrid className="grid">

                    <IonRow class="ion-justify-content-center">

                        {( () => {

                            //Apresentar diferentes menus mediante a seleção
                            switch(this.state.menuOpt) {

                                //Caso a seleção de gráficos seja composição corporal
                                case "compoCorporalOption": 

                                    return(

                                        <React.Fragment>

                                            {/* Gráfico de Evolução do Peso */}

                                            <IonCol sizeXs="12" size-Sm ="12" sizeMd="6" sizeLg="6">

                                                <IonCard className="titleCard">

                                                    <IonCardHeader>
                                                        <IonCardTitle><div className="textWrapper">Peso</div></IonCardTitle>
                                                    </IonCardHeader>

                                                </IonCard>

                                                <div className="evoStats">
                                                    <Line ref="lineChart" data={dataPeso} options={noLegend} />
                                                </div>

                                            </IonCol>

                                            {/* Gráfico de MG e MM */}

                                            <IonCol sizeXs="12" size-Sm ="12" sizeMd="6" sizeLg="6">

                                                <IonCard className="titleCard">

                                                    <IonCardHeader>
                                                        <IonCardTitle><div className="textWrapper">Peso</div></IonCardTitle>
                                                    </IonCardHeader>

                                                </IonCard>

                                                <div className="evoStats">
                                                    <Pie data={dataMG_MM_PIE} />
                                                </div>

                                            </IonCol>

                                        </React.Fragment>
                                    );

                                //Caso a seleção de gráficos seja composição corporal
                                case "perimCorporalOption": 

                                    return(

                                        <React.Fragment>

                                            {/* Gráfico de Evolução do Peso */}
                                            <IonCol sizeXs="12" size-Sm ="12" sizeMd="6" sizeLg="6">

                                                <IonCard className="titleCard">

                                                    <IonCardHeader>
                                                        <IonCardTitle><div className="textWrapper">Cintura</div></IonCardTitle>
                                                    </IonCardHeader>

                                                </IonCard>

                                                <div className="evoStats">
                                                    <Line ref="lineChart" data={dataCintura} options={noLegend} />
                                                </div>

                                            </IonCol>

                                        </React.Fragment>
                                    );

                                    default:

                                        return( 
                                            <IonCol sizeXs="12" size-Sm ="12" sizeMd="6" sizeLg="6">
                                            {/* Empty option */}
                                            </IonCol>
                                        );

                            } //close switch

                        } //close anonymouse function
                    ) ()}

                    </IonRow>

                </IonGrid>

            </IonContent>


        </IonPage>
        );
    }

}

export default Evolucao;
